mapper.insert:
insert 
  into board
     (
       bo_no
     , mem_no
     , bo_title
     , bo_content
     , bo_category
     , bo_cdate
     )
values
     (
       seq_bno.nextval
     , #{memNo}
     , #{boTitle}
     , #{boContent}
     , #{boCategory}
     , SYSDATE
     )

mapper.update:
update
       board
   set 
       bo_title = #{boTitle}
     , bo_content = #{boContent}
     , bo_category = #{boCategory}
     , bo_mdate = SYSDATE
 where bo_no = #{boNo}

mapper.insert:
