mapper.insert:
insert
  into coupon
  (
    coupon_no
  , coupon_name
  , coupon_kind
  , coupon_content
  , coupon_price
  , coupon_pricerate
  , coupon_startdate
  , coupon_enddate
  , coupon_status
  )
  values
  (
    seq_cno.nextval
  , #{couponName}
  , #{couponKind}
  , #{couponContent}
  , #{couponPrice}
  , #{couponPriceRate}
  , #{couponStartDate}
  , #{couponEndDate}
  , default
  )
